int trova(int N) {
	if (N<=2) {
		return N;
	} 

	long long s = 2 + sum[N/STEP], f = fact[N/STEP];
	for(int n = (N-N%STEP + 1); n <= N; ++n) {
		s += f;
		s %= MOD;
		f *= n;
		f %= MOD;
	}
	return s;
}

